const RetirementCalculatorPage = require('../pageobjects/RetirementCalculatorPage');
const validTestData = require('../data/validTestData.json'); 
const invalidTestData = require('../data/invalidTestData.json'); 

describe('Retirement Calculator Tests', () => {
    let retirementPage;

    beforeEach(async () => {
        retirementPage = new RetirementCalculatorPage();
        await browser.url('https://www.securian.com/insights-tools/retirement-calculator.html');
        await browser.maximizeWindow();  
    });
    
    it('should fill the retirement Calculator Form with all required fields (valid data)', async () => {
        await retirementPage.fillForm(validTestData.validData);  
        await retirementPage.toggleSocialSecurity(validTestData.validData);
        await retirementPage.toggleMaritalStatus(validTestData.validData);
        await retirementPage.fillAdditionalFormFields(validTestData.validData);  
        await retirementPage.clickSaveChangesButton();
        await retirementPage.verifyRetirementMessage('Congratulations! You are exceeding your retirement goals. You are saving an extra $833 a month.'); 
       
    });

    it('should update default values and verify changes on the Retirement Calculator Form', async () => {
        await retirementPage.fillForm(validTestData.validDefaultValues);
        await retirementPage.toggleSocialSecurity(validTestData.validDefaultValues);
        await retirementPage.clickDefaultValue();
        await retirementPage.fillForm_DefaultValues(validTestData.validDefaultValues);
        await retirementPage.toggleinflation(validTestData.validDefaultValues);
        await retirementPage.fillForm_AdditionalDefaultValues(validTestData.validDefaultValues);   
        await retirementPage.clickSaveChanges();
        await retirementPage.clickSaveChangesButton();
        await retirementPage.verifyRetirementMessage('Congratulations! You are exceeding your retirement goals. You are saving an extra $833 a month.');
    });

    it('should fill the retirement Calculator Form with invalid data inputs (non-numeric)', async () => {
        await retirementPage.fillForm(invalidTestData.invalidData);  
        await retirementPage.toggleSocialSecurity(invalidTestData.invalidData);  
        await retirementPage.clickSaveChangesButton();  
        await retirementPage.verifyErrorMessage('Please fill out all required fields');
        
    });

    it('should show an error message for age greater than 100', async () => {
        await retirementPage.fillForm(invalidTestData.invalidAge);
        await retirementPage.clickSaveChangesButton();
        await retirementPage.verifyAgeErrorMessage('Planned retirement age must be greater than current age');
    });
    


});

