class BasePage {
  async enterInputField(element, value) {
    try {
      await element.waitForDisplayed({
        timeout: 5000
      });
      await element.clearValue();
      await element.setValue(value);
    } catch (error) {
      console.error(`Error entering input field value: ${error.message}`);
      throw error;
    }
  }
  async enterAge(element, age) {
    try {
      await element.waitForDisplayed({
        timeout: 5000
      });
      await element.setValue(age);
    } catch (error) {
      console.error(`Error entering age: ${error.message}`);
      throw error;
    }
  }
  async enterValue(inputField, value) {
    try {
      await inputField.waitForDisplayed({
        timeout: 5000
      });
      await inputField.clearValue();
      await inputField.setValue('$');
      await browser.keys(['ArrowRight']);
      await inputField.addValue(value);
    } catch (error) {
      console.error(`Error entering value: ${error.message}`);
      throw error;
    }
  }
  async toggleOption(yesOptionSelector, noOptionSelector, toggleValue) {
    try {
      if (toggleValue.toLowerCase() === 'yes') {
        await yesOptionSelector.click();
      } else if (toggleValue.toLowerCase() === 'no') {
        await noOptionSelector.click();
      }
    } catch (error) {
      console.error(`Error toggling option: ${error.message}`);
      throw error;
    }
  }
  async clickButton(locator) {
    try {
      await locator.waitForDisplayed({
        timeout: 5000
      });
      await locator.waitForClickable({
        timeout: 5000
      });
      await locator.moveTo();
      await locator.click();
    } catch (error) {
      console.error(`Error clicking button: ${error.message}`);
      throw error;
    }
  }
  async getText(element) {
    try {
      await element.waitForDisplayed({
        timeout: 5000
      });
      return await element.getText();
    } catch (error) {
      console.error(`Error getting text: ${error.message}`);
      throw error;
    }
  }
}
module.exports = BasePage;
